const SUM_FIED_NAMES = ["total-of-applied-aid", "number-of-practices", "number-of-competitions"];

const xlsxServices = form.getXlsxServices();

const sheetId = xlsxServices.getSheetIdByName("Toiminta-avustus");
const rowCount = xlsxServices.getRowCount(sheetId);
const columnCount = xlsxServices.getColumnCount(sheetId, 0);

for (let columnIndex = 0; columnIndex < columnCount; columnIndex++) {
  const cellSource = xlsxServices.getCellSource(sheetId, 0, columnIndex);
  const cellField = cellSource ? cellSource.getField() : null;
  const fieldType = cellField ? String(cellField.getType()) : null;
  const fieldName = cellField ? String(cellField.getName()) : null;
  const fieldTitle = cellField ? String(cellField.getTitle()) : null;

  if (fieldType === "table" && SUM_FIED_NAMES.indexOf(fieldName) > -1) {
    const tableColumnIndex = columnIndex;
    const sumColumnIndex = tableColumnIndex + 1;
    xlsxServices.insertColumnAfter(sheetId, columnIndex);
    xlsxServices.setCellValueString(sheetId, 0, sumColumnIndex, `Summa - ${fieldTitle || fieldName}`);

    for (let rowIndex = 1; rowIndex < rowCount; rowIndex++) {
      const cell = xlsxServices.getCell(sheetId, rowIndex, columnIndex);
      const link = cell ? cell.getHyperlink() : null;
      if (link) {
        const tableSheetName = link.getAddress();
        const tableSheetId = xlsxServices.getSheetIdByName(tableSheetName);
        const tableRows = xlsxServices.getRowCount(tableSheetId);

        switch (fieldName) {
          case "total-of-applied-aid":
            xlsxServices.setCellFormula(sheetId, rowIndex, columnIndex + 1, xlsxServices.getCellReferenceWithSheet(tableSheetName, tableRows - 1, 1));
          break;
          case "number-of-practices":
            for (let tableRowIndex = 1; tableRowIndex < tableRows; tableRowIndex++) {
              const amountRef = xlsxServices.getCellReferenceWithSheet(tableSheetName, tableRowIndex, 1);
              const participantsRef = xlsxServices.getCellReferenceWithSheet(tableSheetName, tableRowIndex, 2);
              xlsxServices.setCellFormula(tableSheetId, tableRowIndex, 3, `${amountRef} * ${participantsRef}`);  
            }

            xlsxServices.setCellFormula(tableSheetId, tableRows, 3, `SUM(${xlsxServices.getCellReferenceWithSheet(tableSheetName, 1, 3)}:${xlsxServices.getCellReferenceWithSheet(tableSheetName, tableRows - 1, 3)})`);
            xlsxServices.setCellFormula(sheetId, rowIndex, columnIndex + 1, xlsxServices.getCellReferenceWithSheet(tableSheetName, tableRows, 3));
          break;
          case "number-of-competitions":
            for (let tableRowIndex = 1; tableRowIndex < tableRows; tableRowIndex++) {
              const amountRef = xlsxServices.getCellReferenceWithSheet(tableSheetName, tableRowIndex, 1);
              const participantsRef = xlsxServices.getCellReferenceWithSheet(tableSheetName, tableRowIndex, 2);
              xlsxServices.setCellFormula(tableSheetId, tableRowIndex, 3, `${amountRef} * ${participantsRef}`);  
            }

            xlsxServices.setCellFormula(tableSheetId, tableRows, 3, `SUM(${xlsxServices.getCellReferenceWithSheet(tableSheetName, 1, 3)}:${xlsxServices.getCellReferenceWithSheet(tableSheetName, tableRows - 1, 3)})`);
            xlsxServices.setCellFormula(sheetId, rowIndex, columnIndex + 1, xlsxServices.getCellReferenceWithSheet(tableSheetName, tableRows, 3));
          break;
        }        
      }
    }

    columnCount++;
  }
}
